+                System.out.print(blkPieces[-board.array(i)]);
+            } else {
+                System.out.print(whtPieces[board.array(i)]);
+            }
+            if (i % 8 == 7) {
+                System.out.println();
+            } else {
+                System.out.print(" ");
+            }
+        }
+        System.out.println("  A B C D E F G H");
