+        @Override
+        public void writeTo(Writer w) {
+            try {
+                w.write(NAME);
+                w.write(" ");
+                w.write(this.callSiteName);
+                if (!operands().isEmpty()) {
+                    w.write(" ");
+                    for (var op : operands()) {
+                        w.write(op.toString());
+                        w.write(" ");
+                    }
+                }
+
+            } catch (IOException e) {
+                throw new RuntimeException(e);
+            }
+        }
+
+        @Override
+        public String toText() {
+            StringWriter w = new StringWriter();
+            writeTo(w);
+            return w.toString();
+        }
