+                .filter(mm -> mm.methodName().equalsString(methodName)).findFirst().get();
+        var newBytes = ClassFile.of().build(cm.thisClass().asSymbol(), cb -> {
+            for (var ce : cm) {
+                cb.with(ce);
+            }
+            cb.with(builderMethod);
+        });
+        return newBytes;
+    }
