+    public static final class ConstantLabelSwitchChecker {
+        private final MethodHandles.Lookup lookup;
+        private JavaSwitchOp swOp;
+
+        public ConstantLabelSwitchChecker(JavaSwitchOp swOp, MethodHandles.Lookup lookup) {
+            this.swOp = swOp;
