+        private String blockId(Block b) {
+            if (b.index() < 0) return "__" + blockOrdinal++;
+            Op po;
+            Block pb;
+            return (((po = b.parentBody().parentOp()) != null
+                    && (pb = po.parentBlock()) != null)
+                    ? blockId(pb) : "") + "_" + b.index();
+        }
