-  GLIBC_DISABLE(CPU  , AVX512CD)
-  GLIBC_DISABLE(CPU  , AVX512BW)
-  GLIBC_DISABLE(CPU  , AVX512DQ)
-  GLIBC_DISABLE(CPU  , AVX512ER)
-  GLIBC_DISABLE(CPU  , AVX512PF)
-  GLIBC_DISABLE(CPU  , AVX512VL)
