files.patch:
@@ -34,0 +34,1 @@
+#include <dirent.h>
@@ -41,0 +42,1 @@
+#include <sys/types.h>
@@ -51,0 +53,1 @@
+static int g_tty_fd = -1;
@@ -284,0 +287,8 @@
+    // criuengine restorewait should not have foreground; pass it to the java process
+    if (g_tty_fd >= 0) {
+        pid_t current;
+        while ((current = tcgetpgrp(g_tty_fd)) < 0 && errno == EINTR);
+        if (current == getpid()) {
+            while (tcsetpgrp(g_tty_fd, g_pid) && errno == EINTR);
+        }
+    }
@@ -285,1 +296,3 @@
-        kill(g_pid, sig);
+        if (sig != SIGCHLD) {
+            kill(g_pid, sig);
+        }
@@ -290,0 +303,17 @@
+    // standard input and outputs go to /dev/null but
+    // some open FDs can still tell which tty are we part of.
+    DIR *dir = opendir("/proc/self/fd");
+    int dfd = dirfd(dir);
+    struct dirent *dp;
+    while ((dp = readdir(dir))) {
+        if (dp->d_name[0] == '.') {
+            continue; // skip "." and ".."
+        }
+        int fd = atoi(dp->d_name);
+        if (isatty(fd)) {
+            g_tty_fd = fd;
+            break;
+        }
+    }
+    closedir(dir);
+
