+    constexpr const char *sss_nss_memcache = "/var/lib/sss/mc/";
+    if (!strncmp(details, sss_nss_memcache, strlen(sss_nss_memcache))) {
+      print_resources("OK: SSSD cache\n");
+      continue;
+    }
