+  assert(req.is_old() || req.is_young(), "Should request affiliation");
+  return (req.is_old() && _heap->old_generation()->free_unaffiliated_regions() > 0)
+         || (req.is_young() && _heap->young_generation()->free_unaffiliated_regions() > 0);
