+void ShenandoahDirectCardMarkRememberedSet::mark_range_as_dirty(size_t card_index, size_t num_cards) {
+  CardValue* bp = &(_card_table->write_byte_map())[card_index];
+  while (num_cards-- > 0) {
+    *bp++ = CardTable::dirty_card_val();
+  }
