-            try {
-                String fullName = prefix + service.getName();
-                @SuppressWarnings("deprecation")
-                URL u = new URL(url, fullName);
-                boolean found = parse(service, u);
-                if (found)
-                    return true;
-            } catch (MalformedURLException e) {
-                // should not happen; ignore it if it does
-            }
-        }
-        return false;
-    }
-}

