+        // Update the layer field in nodes below the deleted layer
+        for (int l = layerNr; l < getLayerCount(); l++) {
+            for (LayoutNode layoutNode : getLayer(l)) {
+                layoutNode.setLayer(l);
+            }
+        }
+    }
