+    public static void checkCriu() {
+        final boolean hasCriu = Path.of(Utils.TEST_JDK, "lib", "criuengine").toFile().exists();
+        assertEquals("CRIU exists iff we are on Linux", Platform.isLinux(), hasCriu);
+    }
