+bool VM_Version::_ignore_glibc_not_using = false;
+#ifdef LINUX
+const char VM_Version::glibc_prefix[] = ":glibc.cpu.hwcaps=";
+const size_t VM_Version::glibc_prefix_len = strlen(glibc_prefix);
