@@ -26,0 +26,1 @@
+#include <locale.h>
@@ -1170,0 +1171,17 @@
+#ifdef _WINDOWS
+static char *strsep(char **strp, const char *delim) {
+  char *str = *strp;
+  if (str == nullptr) {
+    return nullptr;
+  }
+  size_t len = strcspn(str, delim);
+  if (str[len] == '\0') {
+    *strp = nullptr;
+    return str;
+  }
+  str[len] = '\0';
+  *strp += len + 1;
+  return str;
+}
+#endif // _WINDOWS
+
