@@ -0,0 +1,48 @@
+/* Info boxes */
+.box {
+    margin: 1em 0em;
+    padding: 0 0 .5em .5em;
+    background: #f4f8ff;
+    border-left: 5px solid #437291;
+}
+
+/* Headers in boxes */
+.boxheader {
+    font-weight: bold;
+    padding-top: 0px;
+    margin-bottom: .5em;
+    color: #437291;
+}
+
+/* Notes */
+.note {
+    background-color: #f4f8ff;
+    border-width: 2px;
+    border-color: #437291;
+    border-style: solid none;
+    margin: 1em;
+    padding: 1em 1em 0em 1em;
+}
+
+/* JBS field names */
+.jbs-field {
+    font-weight: bold;
+    color: #333;
+}
+
+/* JBS field values */
+.jbs-value {
+    font-weight: bold;
+    color: #013;
+}
+
+/* JBS labels */
+.jbs-label {
+    font-family: monospace;
+    font-weight: bold;
+    color: #060;
+    white-space: nowrap;
+}
+
+/* Styles below this line are not meant for specific keywords. */
+
