+    public static List<String> select(String prefix, List<String> src) {
+        List<String> result = new ArrayList<>();
+        for (String s : src) {
+            if (s.startsWith(prefix)) {
+                result.add(s.substring(prefix.length()));
+            }
+        }
+        return result;
+    }

