+  } else if (shift == 1) {
+    return SharedRuntime::_jshort_array_copy_ctr;
+  } else if (shift == 2) {
+    return SharedRuntime::_jint_array_copy_ctr;
+  } else {
+    assert(shift == 3, "");
+    return SharedRuntime::_jlong_array_copy_ctr;
+  }
