+  __ BIND(L_copy_byte);
+    __ testl(byte_count, 1);
+    __ jccb(Assembler::zero, L_exit);
+    __ movb(rax, Address(end_from, 8));
+    __ movb(Address(end_to, 8), rax);
+  }
+__ BIND(L_exit);
+  address ucme_exit_pc = __ pc();
+  restore_arg_regs();
