+  __ xorptr(rax, rax); // return 0
+  __ vzeroupper();
+  __ leave(); // required for proper stackwalking of RuntimeStub frame
+  __ ret(0);
+
+  {
+    UnsafeCopyMemoryMark ucmm(this, !aligned, false, ucme_exit_pc);
