+    private static void appendEscapedChar(int idx, int cp, StringBuilder target) {
+        if (cp < 256) {
+            target.append(charRep[cp]);
+        } else if (needsEscape(idx, cp)) {
+            target.append(String.format("\\u%04X", cp));
+        } else {
+            target.appendCodePoint(cp);
+        }
+    }
