+
+        //----------------------------------------------------------------
+
+        expectFail(JAVAC, """
+                -source 8 -target 8
+                -Xbootclasspath/p:GooClass
+                -Xbootclasspath:BadClass${PS}java-lang.jar
+                -Xbootclasspath/a:GooClass
+                Main.java""");
+
+        expectPass(JAVAC, """
+                -source 8 -target 8
