+        int acc = 0;
+        for (int i: inBytes) {
+            acc += i;
+        }
+        return acc;
+    }
+
+    static int applicability1(List<Point> points) {
+        for (IPoint p: points) {
+            System.out.println(p);
+        }
+        return -1;
+    }
+
+    static int applicability2(List<Object> points) {
+        for (Object p: points) {
+            System.out.println(p);
+        }
+        return -1;
+    }
