+
+    public SmartFileObject(JavaFileObject r) {
+        file = r;
+    }
+
+    @Override
+    public boolean equals(Object other) {
+        return file.equals(other);
+    }
+
+    @Override
+    public int hashCode() {
+        return file.hashCode();
+    }
+
+    @DefinedBy(Api.COMPILER)
+    public Kind getKind() {
+        return file.getKind();
+    }
+
+    @DefinedBy(Api.COMPILER)
