+{
+// null
+                aload_0;
+                invokespecial   Method java/lang/Object."<init>":"()V";
+                return;
+}
+
+public static Method main:"([Ljava/lang/String;)V"
+        stack 6 locals 2
+{
+                aload_0;
+                iconst_0;
+                aaload;
+                invokestatic    Method java/lang/Integer.parseInt:"(Ljava/lang/String;)I";
+                istore_1;
+        L12:    return;
+        L13:    iload_1;
+                ifeq    L12;
+                jsr     L23;
+                goto    L24;
+        L23:    return;
+        L24:    aconst_null;
+                goto    L13;
+                return;
+}
