-  struct accelerator_t
-  {
-    accelerator_t (hb_face_t *face)
-    {
-      this->cblc = hb_sanitize_context_t ().reference_table<CBLC> (face);
-      this->cbdt = hb_sanitize_context_t ().reference_table<CBDT> (face);
-
-      upem = hb_face_get_upem (face);
