-      unsigned int cbdt_len = cbdt.get_length ();
-      if (unlikely (image_offset > cbdt_len || cbdt_len - image_offset < image_length))
-        return hb_blob_get_empty ();
-
-      switch (image_format)
-      {
-      case 17:
-      {
-        if (unlikely (image_length < GlyphBitmapDataFormat17::min_size))
