@@ -2174,0 +2174,13 @@
+// Do Vector::rearrange needs preparation of the shuffle argument
+const bool Matcher::vector_needs_load_shuffle(BasicType elem_bt, int vlen) {
+  if (elem_bt == T_BYTE) {
+    return false;
+  } else if (elem_bt == T_SHORT) {
+    return !VM_Version::supports_avx512bw();
+  } else if (elem_bt == T_INT || elem_bt == T_FLOAT) {
+    return !VM_Version::supports_avx2();
+  } else { // T_LONG || T_DOUBLE
+    return vlen < 8 && !VM_Version::supports_avx512vl();
+  }
+}
