+        long buffer = Pollset.allocatePollArray(setsize > 0 ? setsize : 1);
+        int n = Pollset.pollsetPoll(setid, buffer, setsize, subInterval);
+        for(int i=0; i<n; i++) {
+            long eventAddress = Pollset.getEvent(buffer, i);
+            int fd = Pollset.getDescriptor(eventAddress);
