+                if (ignoreFirstLine) {
+                    r.readLine();
+                    sb.append(System.lineSeparator()); // preserve line numbers
+                }
+                char[] buf = new char[1024];
+                int n;
+                while ((n = r.read(buf, 0, buf.length)) != -1)  {
+                    sb.append(buf, 0, n);
+                }
+                return new ProgramFileObject(file, sb, ignoreFirstLine);
+            }
