+                int afterLast = Math.max(1, rnd.nextInt(cslen));
+                int first = Math.max(0, afterLast - (cap - index));
+                cbTarget.put(index, csq, first, afterLast);
+                for (int j = index, i = first; i < afterLast; i++, j++) {
+                    if (cbTarget.get(j) != csq.charAt(i)) {
+                        String s = String.format("Unequal values (seed %d)%n");
+                        throw new RuntimeException(s);
+                    }
+                }
+
