+
+    int neonSize = wordSize * 2;
+    __ ld1(v16, v17, v18, v19, Assembler::T16B, Address(sp, 0));
+    __ add(sp, sp, 4 * neonSize);
+    __ ld1(v4, v5, v6, v7, Assembler::T16B, Address(sp, 0));
