+      __ pop(__ call_clobbered_gp_registers() - RegSet::of(_result), sp);
+    } else {
+      __ pop(__ call_clobbered_gp_registers(), sp);
+    }
+    __ leave();
+  }
+
+public:
+  ZCopyRuntimeCallSpill(MacroAssembler* masm, Register result)
