+    ZCopyRuntimeCallSpill rcs(masm, noreg);
+
+    __ lea(c_rarg0, src);
+
+    __ call_VM_leaf(ZBarrierSetRuntime::store_barrier_on_oop_field_without_healing_addr(), 1);
