+
+  case ZBarrierRelocationFormatStoreGoodBeforeMov:
+    return (uint16_t)ZPointerStoreGoodMask;
+
+  case ZBarrierRelocationFormatStoreBadBeforeMov:
+    return (uint16_t)ZPointerStoreBadMask;
+
+  default:
+    ShouldNotReachHere();
+    return 0;
+  }
+}
