+      if (_xmm_spill_mode == ZXMMSpillMode::avx128) {
+        __ movdqu(Address(rsp, _xmm_size * 7), xmm7);
+        __ movdqu(Address(rsp, _xmm_size * 6), xmm6);
+        __ movdqu(Address(rsp, _xmm_size * 5), xmm5);
