+void XBreakpoint::start_gc() {
+  MonitorLocker ml(ConcurrentGCBreakpoints::monitor());
+  assert(ConcurrentGCBreakpoints::is_controlled(), "Invalid state");
+  assert(!_start_gc, "Invalid state");
+  _start_gc = true;
+  ml.notify_all();
