+
+#include "gc/x/xBitField.hpp"
+#include "memory/allocation.hpp"
+#include "metaprogramming/primitiveConversions.hpp"
+
+#include <type_traits>
+
+class VMStructs;
+
+//
+// Forwarding entry layout
+// -----------------------
+//
+//   6                  4 4
+//   3                  6 5                                                1 0
+//  +--------------------+--------------------------------------------------+-+
+//  |11111111 11111111 11|111111 11111111 11111111 11111111 11111111 1111111|1|
+//  +--------------------+--------------------------------------------------+-+
+//  |                    |                                                  |
+//  |                    |                      0-0 Populated Flag (1-bits) *
+//  |                    |
+//  |                    * 45-1 To Object Offset (45-bits)
+//  |
+//  * 63-46 From Object Index (18-bits)
+//
+
+class XForwardingEntry {
+  friend struct PrimitiveConversions::Translate<XForwardingEntry>;
+  friend class ::VMStructs;
+
+private:
+  typedef XBitField<uint64_t, bool,   0,   1> field_populated;
+  typedef XBitField<uint64_t, size_t, 1,  45> field_to_offset;
+  typedef XBitField<uint64_t, size_t, 46, 18> field_from_index;
+
+  uint64_t _entry;
+
+public:
+  XForwardingEntry() :
+      _entry(0) {}
+
+  XForwardingEntry(size_t from_index, size_t to_offset) :
+      _entry(field_populated::encode(true) |
+             field_to_offset::encode(to_offset) |
+             field_from_index::encode(from_index)) {}
+
+  bool populated() const {
+    return field_populated::decode(_entry);
+  }
+
+  size_t to_offset() const {
+    return field_to_offset::decode(_entry);
+  }
+
+  size_t from_index() const {
+    return field_from_index::decode(_entry);
+  }
+};
+
+// Needed to allow atomic operations on XForwardingEntry
+template <>
+struct PrimitiveConversions::Translate<XForwardingEntry> : public std::true_type {
+  typedef XForwardingEntry Value;
+  typedef uint64_t                  Decayed;
+
+  static Decayed decay(Value v) {
+    return v._entry;
+  }
+
+  static Value recover(Decayed d) {
+    XForwardingEntry entry;
+    entry._entry = d;
+    return entry;
+  }
+};
+
+#endif // SHARE_GC_X_XFORWARDINGENTRY_HPP

