+template <bool Weak>
+class XHeapIteratorRootOopClosure : public OopClosure {
+private:
+  const XHeapIteratorContext& _context;
+
+  oop load_oop(oop* p) {
+    if (Weak) {
+      return NativeAccess<AS_NO_KEEPALIVE | ON_PHANTOM_OOP_REF>::oop_load(p);
+    }
+
+    return NativeAccess<AS_NO_KEEPALIVE>::oop_load(p);
+  }
+
+public:
+  XHeapIteratorRootOopClosure(const XHeapIteratorContext& context) :
+      _context(context) {}
+
+  virtual void do_oop(oop* p) {
+    const oop obj = load_oop(p);
+    _context.mark_and_push(obj);
+  }
+
+  virtual void do_oop(narrowOop* p) {
+    ShouldNotReachHere();
+  }
+};
+
+template <bool VisitReferents>
+class XHeapIteratorOopClosure : public OopIterateClosure {
+private:
+  const XHeapIteratorContext& _context;
+  const oop                   _base;
+
+  oop load_oop(oop* p) {
+    assert(XCollectedHeap::heap()->is_in(p), "Should be in heap");
+
+    if (VisitReferents) {
+      return HeapAccess<AS_NO_KEEPALIVE | ON_UNKNOWN_OOP_REF>::oop_load_at(_base, _base->field_offset(p));
+    }
+
+    return HeapAccess<AS_NO_KEEPALIVE>::oop_load(p);
+  }
+
+public:
+  XHeapIteratorOopClosure(const XHeapIteratorContext& context, oop base) :
+      OopIterateClosure(),
+      _context(context),
+      _base(base) {}
+
+  virtual ReferenceIterationMode reference_iteration_mode() {
+    return VisitReferents ? DO_FIELDS : DO_FIELDS_EXCEPT_REFERENT;
+  }
+
+  virtual void do_oop(oop* p) {
+    const oop obj = load_oop(p);
+    _context.mark_and_push(obj);
+  }
+
+  virtual void do_oop(narrowOop* p) {
+    ShouldNotReachHere();
+  }
+
+  virtual bool do_metadata() {
+    return true;
+  }
+
+  virtual void do_klass(Klass* k) {
+    ClassLoaderData* const cld = k->class_loader_data();
+    XHeapIteratorOopClosure::do_cld(cld);
+  }
+
+  virtual void do_cld(ClassLoaderData* cld) {
+    class NativeAccessClosure : public OopClosure {
+    private:
+      const XHeapIteratorContext& _context;
+
+    public:
+      explicit NativeAccessClosure(const XHeapIteratorContext& context) :
+          _context(context) {}
+
+      virtual void do_oop(oop* p) {
+        assert(!XCollectedHeap::heap()->is_in(p), "Should not be in heap");
+        const oop obj = NativeAccess<AS_NO_KEEPALIVE>::oop_load(p);
+        _context.mark_and_push(obj);
+      }
+
+      virtual void do_oop(narrowOop* p) {
+        ShouldNotReachHere();
+      }
+    };
+
+    NativeAccessClosure cl(_context);
+    cld->oops_do(&cl, ClassLoaderData::_claim_other);
+  }
+
+  // Don't follow loom stack metadata; it's already followed in other ways through CLDs
+  virtual void do_nmethod(nmethod* nm) {}
+  virtual void do_method(Method* m) {}
+};
+
+XHeapIterator::XHeapIterator(uint nworkers, bool visit_weaks) :
+    _visit_weaks(visit_weaks),
+    _timer_disable(),
+    _bitmaps(XAddressOffsetMax),
+    _bitmaps_lock(),
+    _queues(nworkers),
+    _array_queues(nworkers),
+    _roots(ClassLoaderData::_claim_other),
+    _weak_roots(),
+    _terminator(nworkers, &_queues) {
+
+  // Create queues
+  for (uint i = 0; i < _queues.size(); i++) {
+    XHeapIteratorQueue* const queue = new XHeapIteratorQueue();
+    _queues.register_queue(i, queue);
+  }
+
+  // Create array queues
+  for (uint i = 0; i < _array_queues.size(); i++) {
+    XHeapIteratorArrayQueue* const array_queue = new XHeapIteratorArrayQueue();
+    _array_queues.register_queue(i, array_queue);
+  }
+}
+
+XHeapIterator::~XHeapIterator() {
+  // Destroy bitmaps
+  XHeapIteratorBitMapsIterator iter(&_bitmaps);
+  for (XHeapIteratorBitMap* bitmap; iter.next(&bitmap);) {
+    delete bitmap;
+  }
+
+  // Destroy array queues
+  for (uint i = 0; i < _array_queues.size(); i++) {
+    delete _array_queues.queue(i);
+  }
+
+  // Destroy queues
+  for (uint i = 0; i < _queues.size(); i++) {
+    delete _queues.queue(i);
+  }
+
+  // Clear claimed CLD bits
+  ClassLoaderDataGraph::clear_claimed_marks(ClassLoaderData::_claim_other);
+}
+
+static size_t object_index_max() {
+  return XGranuleSize >> XObjectAlignmentSmallShift;
+}
+
+static size_t object_index(oop obj) {
+  const uintptr_t addr = XOop::to_address(obj);
+  const uintptr_t offset = XAddress::offset(addr);
+  const uintptr_t mask = XGranuleSize - 1;
+  return (offset & mask) >> XObjectAlignmentSmallShift;
+}
+
+XHeapIteratorBitMap* XHeapIterator::object_bitmap(oop obj) {
+  const uintptr_t offset = XAddress::offset(XOop::to_address(obj));
+  XHeapIteratorBitMap* bitmap = _bitmaps.get_acquire(offset);
+  if (bitmap == NULL) {
+    XLocker<XLock> locker(&_bitmaps_lock);
+    bitmap = _bitmaps.get(offset);
+    if (bitmap == NULL) {
+      // Install new bitmap
+      bitmap = new XHeapIteratorBitMap(object_index_max());
+      _bitmaps.release_put(offset, bitmap);
+    }
+  }
+
+  return bitmap;
+}
+
+bool XHeapIterator::mark_object(oop obj) {
+  if (obj == NULL) {
+    return false;
+  }
+
+  XHeapIteratorBitMap* const bitmap = object_bitmap(obj);
+  const size_t index = object_index(obj);
+  return bitmap->try_set_bit(index);
+}
