+typedef ClaimingCLDToOopClosure<ClassLoaderData::_claim_other> XHeapIteratorCLDCLosure;
+
+class XHeapIteratorNMethodClosure : public NMethodClosure {
+private:
+  OopClosure* const        _cl;
+  BarrierSetNMethod* const _bs_nm;
+
+public:
+  XHeapIteratorNMethodClosure(OopClosure* cl) :
+      _cl(cl),
+      _bs_nm(BarrierSet::barrier_set()->barrier_set_nmethod()) {}
+
+  virtual void do_nmethod(nmethod* nm) {
+    // If ClassUnloading is turned off, all nmethods are considered strong,
+    // not only those on the call stacks. The heap iteration might happen
+    // before the concurrent processign of the code cache, make sure that
+    // all nmethods have been processed before visiting the oops.
+    _bs_nm->nmethod_entry_barrier(nm);
+
+    XNMethod::nmethod_oops_do(nm, _cl);
+  }
+};
+
+class XHeapIteratorThreadClosure : public ThreadClosure {
+private:
+  OopClosure* const        _cl;
+  CodeBlobToNMethodClosure _cb_cl;
+
+public:
+  XHeapIteratorThreadClosure(OopClosure* cl, NMethodClosure* nm_cl) :
+      _cl(cl),
+      _cb_cl(nm_cl) {}
+
+  void do_thread(Thread* thread) {
+    thread->oops_do(_cl, &_cb_cl);
+  }
+};
+
+void XHeapIterator::push_strong_roots(const XHeapIteratorContext& context) {
+  XHeapIteratorRootOopClosure<false /* Weak */> cl(context);
+  XHeapIteratorCLDCLosure cld_cl(&cl);
+  XHeapIteratorNMethodClosure nm_cl(&cl);
+  XHeapIteratorThreadClosure thread_cl(&cl, &nm_cl);
+
+  _roots.apply(&cl,
+               &cld_cl,
+               &thread_cl,
+               &nm_cl);
+}
+
+void XHeapIterator::push_weak_roots(const XHeapIteratorContext& context) {
+  XHeapIteratorRootOopClosure<true  /* Weak */> cl(context);
+  _weak_roots.apply(&cl);
+}
+
+template <bool VisitWeaks>
+void XHeapIterator::push_roots(const XHeapIteratorContext& context) {
+  push_strong_roots(context);
+  if (VisitWeaks) {
+    push_weak_roots(context);
+  }
+}
+
+template <bool VisitReferents>
+void XHeapIterator::follow_object(const XHeapIteratorContext& context, oop obj) {
+  XHeapIteratorOopClosure<VisitReferents> cl(context, obj);
+  obj->oop_iterate(&cl);
+}
+
+void XHeapIterator::follow_array(const XHeapIteratorContext& context, oop obj) {
+  // Follow klass
+  XHeapIteratorOopClosure<false /* VisitReferents */> cl(context, obj);
+  cl.do_klass(obj->klass());
+
+  // Push array chunk
+  context.push_array(ObjArrayTask(obj, 0 /* index */));
+}
+
+void XHeapIterator::follow_array_chunk(const XHeapIteratorContext& context, const ObjArrayTask& array) {
+  const objArrayOop obj = objArrayOop(array.obj());
+  const int length = obj->length();
+  const int start = array.index();
+  const int stride = MIN2<int>(length - start, ObjArrayMarkingStride);
+  const int end = start + stride;
+
+  // Push remaining array chunk first
+  if (end < length) {
+    context.push_array(ObjArrayTask(obj, end));
+  }
+
+  // Follow array chunk
+  XHeapIteratorOopClosure<false /* VisitReferents */> cl(context, obj);
+  obj->oop_iterate_range(&cl, start, end);
+}
+
+template <bool VisitWeaks>
+void XHeapIterator::visit_and_follow(const XHeapIteratorContext& context, ObjectClosure* cl, oop obj) {
+  // Visit
+  cl->do_object(obj);
+
+  // Follow
+  if (obj->is_objArray()) {
+    follow_array(context, obj);
+  } else {
+    follow_object<VisitWeaks>(context, obj);
+  }
+}
+
+template <bool VisitWeaks>
+void XHeapIterator::drain(const XHeapIteratorContext& context, ObjectClosure* cl) {
+  ObjArrayTask array;
+  oop obj;
+
+  do {
+    while (context.pop(obj)) {
+      visit_and_follow<VisitWeaks>(context, cl, obj);
+    }
+
+    if (context.pop_array(array)) {
+      follow_array_chunk(context, array);
+    }
+  } while (!context.is_drained());
+}
+
+template <bool VisitWeaks>
+void XHeapIterator::steal(const XHeapIteratorContext& context, ObjectClosure* cl) {
+  ObjArrayTask array;
+  oop obj;
+
+  if (context.steal_array(array)) {
+    follow_array_chunk(context, array);
+  } else if (context.steal(obj)) {
+    visit_and_follow<VisitWeaks>(context, cl, obj);
+  }
+}
+
+template <bool VisitWeaks>
+void XHeapIterator::drain_and_steal(const XHeapIteratorContext& context, ObjectClosure* cl) {
+  do {
+    drain<VisitWeaks>(context, cl);
+    steal<VisitWeaks>(context, cl);
+  } while (!context.is_drained() || !_terminator.offer_termination());
+}
+
+template <bool VisitWeaks>
+void XHeapIterator::object_iterate_inner(const XHeapIteratorContext& context, ObjectClosure* object_cl) {
+  push_roots<VisitWeaks>(context);
+  drain_and_steal<VisitWeaks>(context, object_cl);
+}
+
+void XHeapIterator::object_iterate(ObjectClosure* cl, uint worker_id) {
+  XHeapIteratorContext context(this, worker_id);
+
+  if (_visit_weaks) {
+    object_iterate_inner<true /* VisitWeaks */>(context, cl);
+  } else {
+    object_iterate_inner<false /* VisitWeaks */>(context, cl);
+  }
+}

