+void XMark::verify_all_stacks_empty() const {
+  // Verify thread stacks
+  XVerifyMarkStacksEmptyClosure cl(&_stripes);
+  Threads::threads_do(&cl);
+
+  // Verify stripe stacks
+  guarantee(_stripes.is_empty(), "Should be empty");
