+uintptr_t XMemoryManager::peek_low_address() const {
+  XLocker<XLock> locker(&_lock);
+
+  const XMemory* const area = _freelist.first();
+  if (area != NULL) {
+    return area->start();
+  }
+
+  // Out of memory
+  return UINTPTR_MAX;
