+//
+// Stat sampler/counter data
+//
+struct XStatSamplerData {
+  uint64_t _nsamples;
+  uint64_t _sum;
+  uint64_t _max;
+
+  XStatSamplerData() :
+    _nsamples(0),
+    _sum(0),
+    _max(0) {}
+
+  void add(const XStatSamplerData& new_sample) {
+    _nsamples += new_sample._nsamples;
+    _sum += new_sample._sum;
+    _max = MAX2(_max, new_sample._max);
+  }
+};
+
+struct XStatCounterData {
+  uint64_t _counter;
+
+  XStatCounterData() :
+    _counter(0) {}
+};
