+  log_info(gc)("Garbage Collection (%s) " XSIZE_FMT "->" XSIZE_FMT,
+               GCCause::to_string(XCollectedHeap::heap()->gc_cause()),
+               XSIZE_ARGS(XStatHeap::used_at_mark_start()),
