+THREAD_LOCAL bool      XThread::_initialized;
+THREAD_LOCAL uintptr_t XThread::_id;
+THREAD_LOCAL bool      XThread::_is_vm;
+THREAD_LOCAL bool      XThread::_is_java;
+THREAD_LOCAL bool      XThread::_is_worker;
+THREAD_LOCAL uint      XThread::_worker_id;
+
+void XThread::initialize() {
+  assert(!_initialized, "Already initialized");
+  const Thread* const thread = Thread::current();
+  _initialized = true;
+  _id = (uintptr_t)thread;
+  _is_vm = thread->is_VM_thread();
+  _is_java = thread->is_Java_thread();
+  _is_worker = false;
+  _worker_id = (uint)-1;
+}
+
+const char* XThread::name() {
+  const Thread* const thread = Thread::current();
+  if (thread->is_Named_thread()) {
+    const NamedThread* const named = (const NamedThread*)thread;
+    return named->name();
+  } else if (thread->is_Java_thread()) {
+    return "Java";
+  }
+
+  return "Unknown";
+}
+
+void XThread::set_worker() {
+  ensure_initialized();
+  _is_worker = true;
+}
+
+bool XThread::has_worker_id() {
+  return _initialized &&
+         _is_worker &&
+         _worker_id != (uint)-1;
+}
+
+void XThread::set_worker_id(uint worker_id) {
+  ensure_initialized();
+  assert(!has_worker_id(), "Worker id already initialized");
+  _worker_id = worker_id;
+}
+
+void XThread::clear_worker_id() {
+  assert(has_worker_id(), "Worker id not initialized");
+  _worker_id = (uint)-1;
+}

