+#include "precompiled.hpp"
+#include "gc/shared/gc_globals.hpp"
+#include "gc/x/xHeap.inline.hpp"
+#include "gc/x/xLock.inline.hpp"
+#include "gc/x/xStat.hpp"
+#include "gc/x/xUncommitter.hpp"
+#include "jfr/jfrEvents.hpp"
+#include "logging/log.hpp"
+
+static const XStatCounter XCounterUncommit("Memory", "Uncommit", XStatUnitBytesPerSecond);
+
+XUncommitter::XUncommitter(XPageAllocator* page_allocator) :
+    _page_allocator(page_allocator),
+    _lock(),
+    _stop(false) {
+  set_name("XUncommitter");
+  create_and_start();
+}
+
+bool XUncommitter::wait(uint64_t timeout) const {
+  XLocker<XConditionLock> locker(&_lock);
+  while (!ZUncommit && !_stop) {
+    _lock.wait();
+  }
+
+  if (!_stop && timeout > 0) {
+    log_debug(gc, heap)("Uncommit Timeout: " UINT64_FORMAT "s", timeout);
+    _lock.wait(timeout * MILLIUNITS);
+  }
+
+  return !_stop;
+}
+
+bool XUncommitter::should_continue() const {
+  XLocker<XConditionLock> locker(&_lock);
+  return !_stop;
+}
+
+void XUncommitter::run_service() {
+  uint64_t timeout = 0;
+
+  while (wait(timeout)) {
+    EventZUncommit event;
+    size_t uncommitted = 0;
+
+    while (should_continue()) {
+      // Uncommit chunk
+      const size_t flushed = _page_allocator->uncommit(&timeout);
+      if (flushed == 0) {
+        // Done
+        break;
+      }
+
+      uncommitted += flushed;
+    }
+
+    if (uncommitted > 0) {
+      // Update statistics
+      XStatInc(XCounterUncommit, uncommitted);
+      log_info(gc, heap)("Uncommitted: " SIZE_FORMAT "M(%.0f%%)",
+                         uncommitted / M, percent_of(uncommitted, XHeap::heap()->max_capacity()));
+
+      // Send event
+      event.commit(uncommitted);
+    }
+  }
+}
+
+void XUncommitter::stop_service() {
+  XLocker<XConditionLock> locker(&_lock);
+  _stop = true;
+  _lock.notify_all();
+}

