+//
+//             vv-- overlapping address and metadata zeros
+//   aaa...aaa00010MMmmFFrr0000 = Remapped01 zpointer
+//
+//             v--- overlapping address and metadata zero
+//  aaa...aaa000100MMmmFFrr0000 = Remapped10 zpointer
+//
