+ZPageAge ZRelocate::compute_to_age(ZPageAge from_age) {
+  if (from_age == ZPageAge::old) {
+    return ZPageAge::old;
+  }
+
+  const uint age = static_cast<uint>(from_age);
+  if (age >= ZGeneration::young()->tenuring_threshold()) {
+    return ZPageAge::old;
+  }
+
+  return static_cast<ZPageAge>(age + 1);
+}
