+    private long fieldFromIndexDecode(long value) {
+        long FieldMask = (1L << 18) - 1;
+        int FieldShift = 46;
+        int ValueShift = 0;
+        return ((value >>> FieldShift) & FieldMask) << ValueShift;
+    }
