+    private class XPagesIterator implements Iterator<XPage> {
+        private XGranuleMapForPageTable.Iterator mapIter;
+        private XPage next;
+
+        XPagesIterator() {
+            mapIter = map().new Iterator();
+            positionToNext();
+        }
+
+        private XPage positionToNext() {
+            XPage current = next;
